theme.pfx:script:
var calcProxy = new WS_Calculator();
function add() {
  var a = document.getElementById('a').value;
  var b = document.getElementById('b').value;
  alert(calcProxy.add(a,b));
}
function subtract() {
  var a = document.getElementById('a').value;
  var b = document.getElementById('b').value;
  alert(calcProxy.subtract(a,b));
}
function multiply() {
  var a = document.getElementById('a').value;
  var b = document.getElementById('b').value;
  alert(calcProxy.multiply(a,b));
}
